import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  Button
} from '@mui/material';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import api from '../services/api';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function Laporan() {
  // State ringkasan Toko, Investor, Dividen
  const [tokoCount, setTokoCount] = useState(0);
  const [investorCount, setInvestorCount] = useState(0);
  const [dividenCount, setDividenCount] = useState(0);

  // Misal totalProfit & totalBagi (akumulasi) di Toko
  const [totalProfitToko, setTotalProfitToko] = useState(0);
  const [totalBagiToko, setTotalBagiToko] = useState(0);

  // Misal sum totalProfit di Dividen
  const [sumProfitDividen, setSumProfitDividen] = useState(0);

  // Chart data
  const [chartTokoData, setChartTokoData] = useState({
    labels: [],
    datasets: []
  });

  // Chart data Dividen
  const [chartDividenData, setChartDividenData] = useState({
    labels: [],
    datasets: []
  });

  // Fetch ringkasan
  const fetchStats = async()=>{
    try {
      // 1) Toko
      const resToko = await api.get('/finance/toko');
      const tokoList = resToko.data;
      setTokoCount(tokoList.length);

      let sumProfit = 0;
      let sumBagi = 0;
      tokoList.forEach(tk => {
        sumProfit += parseFloat(tk.totalProfit || 0);
        sumBagi += parseFloat(tk.totalBagi || 0);
      });
      setTotalProfitToko(sumProfit);
      setTotalBagiToko(sumBagi);

      // Buat chart data Toko (top 5 by totalProfit)
      const sortedToko = tokoList.sort((a,b)=>(b.totalProfit||0)-(a.totalProfit||0)).slice(0,5);
      const tokoLabels = sortedToko.map(t=>t.nama);
      const tokoProfits = sortedToko.map(t=>parseFloat(t.totalProfit||0));

      setChartTokoData({
        labels: tokoLabels,
        datasets: [
          {
            label: 'Top 5 Profit Toko',
            data: tokoProfits,
            backgroundColor: '#7E57C2'
          }
        ]
      });

      // 2) Investor
      const resInv = await api.get('/finance/investor');
      const invList = resInv.data;
      setInvestorCount(invList.length);

      // 3) Dividen
      const resDiv = await api.get('/finance/dividen');
      const divList = resDiv.data;
      setDividenCount(divList.length);

      let sumDivProfit = 0;
      divList.forEach(dv => {
        sumDivProfit += parseFloat(dv.totalProfit || 0);
      });
      setSumProfitDividen(sumDivProfit);

      // Chart Dividen (by distributedAt date)
      // misal kita bikin bar chart: X-axis = date, Y-axis = totalProfit
      const sortedDiv = divList.sort((a,b)=> new Date(a.distributedAt) - new Date(b.distributedAt));
      const divLabels = sortedDiv.map(d=> new Date(d.distributedAt).toLocaleDateString());
      const divProfits = sortedDiv.map(d=> parseFloat(d.totalProfit||0));

      setChartDividenData({
        labels: divLabels,
        datasets: [
          {
            label: 'Dividen Profit',
            data: divProfits,
            backgroundColor: '#5C6BC0'
          }
        ]
      });

    } catch(err){
      console.error(err);
      alert('Gagal memuat laporan');
    }
  };

  useEffect(()=>{
    fetchStats();
  },[]);

  // Contoh export
  const handleExport = async()=>{
    try {
      // const res = await api.get('/finance/export', { responseType: 'blob' });
      // ...
      alert('Export belum diimplementasi');
    } catch(err){
      alert('Export gagal');
    }
  };

  return (
    <Box sx={{ p:2 }}>
      <Typography variant="h4" sx={{ fontWeight:700, mb:3 }}>
        Laporan Keuangan
      </Typography>

      <Grid container spacing={2} sx={{ mb:3 }}>
        {/* Card Toko */}
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p:2, background:"linear-gradient(135deg, #512DA8 30%, #303F9F 100%)", color:"#fff" }}>
            <Typography variant="body2" sx={{ opacity:0.7 }}>Total Toko</Typography>
            <Typography variant="h5" sx={{ fontWeight:700 }}>
              {tokoCount}
            </Typography>
          </Paper>
        </Grid>

        {/* Card Investor */}
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p:2, background:"linear-gradient(135deg, #7E57C2 30%, #5C6BC0 100%)", color:"#fff" }}>
            <Typography variant="body2" sx={{ opacity:0.7 }}>Total Investor</Typography>
            <Typography variant="h5" sx={{ fontWeight:700 }}>
              {investorCount}
            </Typography>
          </Paper>
        </Grid>

        {/* Card Dividen */}
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p:2, background:"linear-gradient(135deg, #5E35B1 30%, #3949AB 100%)", color:"#fff" }}>
            <Typography variant="body2" sx={{ opacity:0.7 }}>Total Dividen</Typography>
            <Typography variant="h5" sx={{ fontWeight:700 }}>
              {dividenCount}
            </Typography>
          </Paper>
        </Grid>

        {/* Card Toko Profit */}
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p:2, background:"linear-gradient(135deg, #AB47BC 30%, #7E57C2 100%)", color:"#fff" }}>
            <Typography variant="body2" sx={{ opacity:0.7 }}>Total Profit (Toko)</Typography>
            <Typography variant="h6" sx={{ fontWeight:700 }}>
              {totalProfitToko.toLocaleString()}
            </Typography>
            <Typography variant="body2" sx={{ mt:1 }}>
              Bagi: {totalBagiToko.toLocaleString()}
            </Typography>
          </Paper>
        </Grid>
      </Grid>

      {/* Chart Toko */}
      <Paper sx={{ p:2, mb:3 }}>
        <Typography variant="h6" sx={{ mb:2, fontWeight:700 }}>
          Top 5 Toko berdasarkan Profit
        </Typography>
        <Box sx={{ height:300 }}>
          <Bar
            data={chartTokoData}
            options={{
              responsive:true,
              plugins:{ legend:{ position:'top'} }
            }}
          />
        </Box>
      </Paper>

      {/* Chart Dividen */}
      <Paper sx={{ p:2, mb:3 }}>
        <Typography variant="h6" sx={{ mb:2, fontWeight:700 }}>
          History Dividen (By Date)
        </Typography>
        <Typography variant="body2" sx={{ mb:1 }}>
          Total Profit Dividen: {sumProfitDividen.toLocaleString()}
        </Typography>
        <Box sx={{ height:300 }}>
          <Bar
            data={chartDividenData}
            options={{
              responsive:true,
              plugins:{ legend:{ position:'top'} }
            }}
          />
        </Box>
      </Paper>

      {/* Tombol Export */}
      <Button variant="contained" onClick={handleExport}>
        Export Laporan CSV
      </Button>
    </Box>
  );
}
