import React, { useState } from "react";
import { Box, useMediaQuery } from "@mui/material";
import Navbar from "./Navbar";
import Sidebar from "./Sidebar";

export default function Layout() {
  // State untuk toggling sidebar
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Cek apakah layar besar (desktop)
  const isDesktop = useMediaQuery("(min-width:1024px)");

  const handleDrawerToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Di layar desktop, kita bisa pakai "persistent" Drawer,
  // sedangkan di mobile pakai "temporary" Drawer.
  // Atau kita bisa memaksa "temporary" di semua ukuran; sesuai preferensi.

  return (
    <Box sx={{ display: "flex", minHeight: "100vh" }}>
      <Sidebar
        sidebarOpen={sidebarOpen}
        handleDrawerToggle={handleDrawerToggle}
        isDesktop={isDesktop}
      />
      <Box sx={{ flexGrow: 1, display: "flex", flexDirection: "column" }}>
        <Navbar onMenuClick={handleDrawerToggle} />
        {/* Konten */}
        <Box component="main" sx={{ p: 2, flexGrow: 1 }}>
          {/* Nanti diisi Outlet atau children */}
        </Box>
      </Box>
    </Box>
  );
}
