import React, { useEffect, useState } from 'react';
import {
  Box, Typography, Paper, Table, TableHead,
  TableRow, TableCell, TableBody, TextField, Button
} from '@mui/material';
import api from '../services/api';

export default function Toko() {
  const [tokoData, setTokoData] = useState([]);

  // State untuk form input
  const [nama, setNama] = useState('');
  const [modalAwal, setModalAwal] = useState('');

  const fetchToko = async () => {
    try {
      const res = await api.get('/finance/toko'); // GET data Toko
      setTokoData(res.data);
    } catch (err) {
      console.error(err);
      alert('Gagal memuat data Toko');
    }
  };

  // Pertama kali load, fetch data
  useEffect(() => {
    fetchToko();
  }, []);

  // Fungsi untuk handle submit form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Kirim POST ke /finance/toko
      await api.post('/finance/toko', {
        nama,
        modalAwal
      });
      // Reset form
      setNama('');
      setModalAwal('');
      // Re-fetch data
      fetchToko();
    } catch(err) {
      console.error(err);
      alert('Gagal menambah Toko');
    }
  };

  return (
    <Box>
      <Typography variant="h5" sx={{ fontWeight:700, mb:2 }}>
        Data Toko
      </Typography>

      {/* FORM INPUT */}
      <Paper sx={{ p:2, mb:3 }}>
        <Typography variant="h6" sx={{ fontWeight:600, mb:2 }}>
          Tambah Toko
        </Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Nama Toko"
            value={nama}
            onChange={(e) => setNama(e.target.value)}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Modal Awal"
            type="number"
            value={modalAwal}
            onChange={(e) => setModalAwal(e.target.value)}
            fullWidth
            margin="normal"
          />
          <Button type="submit" variant="contained" sx={{ mt:2 }}>
            Tambah
          </Button>
        </form>
      </Paper>

      {/* TABEL DATA */}
      <Paper sx={{ p:2 }}>
        <Table>
          <TableHead>
            <TableRow sx={{ backgroundColor:'#f5f5f5' }}>
              <TableCell>ID</TableCell>
              <TableCell>Nama</TableCell>
              <TableCell>Modal Awal</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tokoData.map((t)=>(
              <TableRow key={t.id}>
                <TableCell>{t.id}</TableCell>
                <TableCell>{t.nama}</TableCell>
                <TableCell>{t.modalAwal}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Box>
  );
}
