import React, { useState } from 'react';
import { Container, Box, TextField, Button, Typography } from '@mui/material';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async(e) => {
    e.preventDefault();
    try {
      const res = await api.post('/auth/login', { email, password });
      const { token, role } = res.data;
      localStorage.setItem('token', token);
      localStorage.setItem('role', role); 
      // server harus mengirim { token, role } agar front-end tahu
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Login Error');
    }
  };

  return (
    <Container maxWidth="xs" sx={{ mt: 5 }}>
      <Box sx={{ p:3, bgcolor:'white', borderRadius:2, boxShadow:'0 2px 8px rgba(0,0,0,0.1)' }}>
        <Typography variant="h5" sx={{ mb:2, fontWeight:700 }}>Login</Typography>
        <form onSubmit={handleSubmit}>
          <TextField 
            label="Email"
            value={email}
            onChange={(e)=>setEmail(e.target.value)}
            fullWidth
            margin="normal"
          />
          <TextField 
            label="Password"
            type="password"
            value={password}
            onChange={(e)=>setPassword(e.target.value)}
            fullWidth
            margin="normal"
          />
          <Button variant="contained" type="submit" sx={{ mt:2 }} fullWidth>
            Login
          </Button>
        </form>
      </Box>
    </Container>
  );
}
