import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TextField,
  Button,
  Select,
  MenuItem,
  IconButton
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import api from '../services/api';

export default function Kepemilikan() {
  const [ownerships, setOwnerships] = useState([]);
  const [allToko, setAllToko] = useState([]);
  const [allInvestor, setAllInvestor] = useState([]);

  // State form
  const [tokoId, setTokoId] = useState('');
  const [investorId, setInvestorId] = useState('');
  const [persentase, setPersentase] = useState('');

  // Tambahan
  const [modalAwalInv, setModalAwalInv] = useState('');
  const [totalInjectInv, setTotalInjectInv] = useState('');
  const [totalBagiInv, setTotalBagiInv] = useState('');

  const [editingId, setEditingId] = useState(null);

  useEffect(()=>{
    fetchOwnership();
    fetchToko();
    fetchInvestor();
  },[]);

  const fetchOwnership = async()=>{
    try {
      const res = await api.get('/finance/ownership');
      setOwnerships(res.data);
    } catch(err){
      alert('Gagal memuat data Kepemilikan');
    }
  };

  const fetchToko = async()=>{
    try {
      const res = await api.get('/finance/toko');
      setAllToko(res.data);
    } catch(err){
      alert('Gagal memuat Toko');
    }
  };

  const fetchInvestor = async()=>{
    try {
      const res = await api.get('/finance/investor');
      setAllInvestor(res.data);
    } catch(err){
      alert('Gagal memuat Investor');
    }
  };

  const handleSubmit = async(e)=>{
    e.preventDefault();
    try {
      const payload = {
        tokoId,
        investorId,
        persentase,
        modalAwalInv,
        totalInjectInv,
        totalBagiInv
      };
      if(editingId){
        await api.put(`/finance/ownership/${editingId}`, payload);
      } else {
        await api.post('/finance/ownership', payload);
      }
      setTokoId('');
      setInvestorId('');
      setPersentase('');
      setModalAwalInv('');
      setTotalInjectInv('');
      setTotalBagiInv('');
      setEditingId(null);
      fetchOwnership();
    } catch(err){
      console.error(err);
      alert('Gagal submit kepemilikan');
    }
  };

  const handleEdit = (item)=>{
    setEditingId(item.id);
    setTokoId(item.tokoId);
    setInvestorId(item.investorId);
    setPersentase(item.persentase || '');
    setModalAwalInv(item.modalAwalInv || '');
    setTotalInjectInv(item.totalInjectInv || '');
    setTotalBagiInv(item.totalBagiInv || '');
  };

  const handleDelete = async(id)=>{
    if(!window.confirm('Hapus data kepemilikan ini?')) return;
    try {
      await api.delete(`/finance/ownership/${id}`);
      fetchOwnership();
    } catch(err){
      alert('Gagal hapus kepemilikan');
    }
  };

  return (
    <Box>
      <Typography variant="h5" sx={{ fontWeight:700, mb:2 }}>
        Kepemilikan Saham
      </Typography>

      {/* FORM INPUT / EDIT */}
      <Paper sx={{ p:2, mb:3 }}>
        <Typography variant="h6" sx={{ fontWeight:600, mb:2 }}>
          { editingId ? 'Edit Kepemilikan' : 'Tambah Kepemilikan' }
        </Typography>

        <form onSubmit={handleSubmit}>
          <Select
            value={tokoId}
            onChange={(e)=>setTokoId(e.target.value)}
            fullWidth
            sx={{ mt:1, mb:2 }}
          >
            <MenuItem value="">-- Pilih Toko --</MenuItem>
            {allToko.map((t)=>(
              <MenuItem key={t.id} value={t.id}>
                {t.nama}
              </MenuItem>
            ))}
          </Select>

          <Select
            value={investorId}
            onChange={(e)=>setInvestorId(e.target.value)}
            fullWidth
            sx={{ mb:2 }}
          >
            <MenuItem value="">-- Pilih Investor --</MenuItem>
            {allInvestor.map((inv)=>(
              <MenuItem key={inv.id} value={inv.id}>
                {inv.namaInvestor}
              </MenuItem>
            ))}
          </Select>

          <TextField
            label="Persentase Saham (%)"
            value={persentase}
            onChange={(e)=>setPersentase(e.target.value)}
            fullWidth
            margin="normal"
          />
          {/* Tambahan field sesuai spreadsheet */}
          <TextField
            label="Modal Awal Investor"
            type="number"
            value={modalAwalInv}
            onChange={(e)=>setModalAwalInv(e.target.value)}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Total Inject Investor"
            type="number"
            value={totalInjectInv}
            onChange={(e)=>setTotalInjectInv(e.target.value)}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Total Bagi Investor"
            type="number"
            value={totalBagiInv}
            onChange={(e)=>setTotalBagiInv(e.target.value)}
            fullWidth
            margin="normal"
          />

          <Button variant="contained" type="submit" sx={{ mt:2 }}>
            { editingId ? 'Update' : 'Simpan' }
          </Button>
        </form>
      </Paper>

      {/* TABEL DATA */}
      <Paper sx={{ p:2 }}>
        <Table>
          <TableHead>
            <TableRow sx={{ backgroundColor:'#f5f5f5' }}>
              <TableCell>ID</TableCell>
              <TableCell>Toko</TableCell>
              <TableCell>Investor</TableCell>
              <TableCell>Persentase</TableCell>
              <TableCell>Modal Awal Inv</TableCell>
              <TableCell>Total Inject Inv</TableCell>
              <TableCell>Total Bagi Inv</TableCell>
              <TableCell>Aksi</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {ownerships.map((o)=>(
              <TableRow key={o.id}>
                <TableCell>{o.id}</TableCell>
                <TableCell>{o.Toko ? o.Toko.nama : o.tokoId}</TableCell>
                <TableCell>{o.Investor ? o.Investor.namaInvestor : o.investorId}</TableCell>
                <TableCell>{o.persentase}</TableCell>
                <TableCell>{o.modalAwalInv}</TableCell>
                <TableCell>{o.totalInjectInv}</TableCell>
                <TableCell>{o.totalBagiInv}</TableCell>
                <TableCell>
                  <IconButton onClick={()=>handleEdit(o)}>
                    <EditIcon/>
                  </IconButton>
                  <IconButton onClick={()=>handleDelete(o.id)}>
                    <DeleteIcon color="error"/>
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Box>
  );
}
