import React from "react";
import {
  Drawer,
  List,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Box,
  Typography,
  Divider,
} from "@mui/material";
import { Link, useLocation } from "react-router-dom";
import DashboardIcon from "@mui/icons-material/Dashboard";
import StoreIcon from "@mui/icons-material/Store";
import GroupsIcon from "@mui/icons-material/Groups";
import PaymentsIcon from "@mui/icons-material/Payments";
import HistoryEduIcon from "@mui/icons-material/HistoryEdu";
import ReceiptLongIcon from "@mui/icons-material/ReceiptLong";

const drawerWidth = 240;

export default function Sidebar({ sidebarOpen, handleDrawerToggle, isDesktop }) {
  const location = useLocation();

  const menuItems = [
    { text: "Dashboard", path: "/", icon: <DashboardIcon /> },
    { text: "Toko", path: "/toko", icon: <StoreIcon /> },
    { text: "Investor", path: "/investor", icon: <GroupsIcon /> },
    { text: "Dividen", path: "/dividen", icon: <PaymentsIcon /> },
    { text: "Histori", path: "/histori", icon: <HistoryEduIcon /> },
    { text: "Transactions", path: "/transactions", icon: <ReceiptLongIcon /> },
  ];

  // Tipe drawer bergantung mode
  // - "persistent" jika desktop, agar sidebar menempel
  // - "temporary" jika mobile, agar menutupi dan bisa di-swipe out
  const drawerVariant = isDesktop ? "persistent" : "temporary";

  return (
    <Drawer
      variant={drawerVariant}
      open={sidebarOpen || isDesktop} // jika desktop, always open; jika mobile, tergantung state
      onClose={handleDrawerToggle} // close if click outside (temporary mode)
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        "& .MuiDrawer-paper": {
          width: drawerWidth,
          boxSizing: "border-box",
          borderRight: "1px solid #e0e0e0",
          // Gradient futuristik ungu-biru:
          background: "linear-gradient(135deg, #7e57c2 30%, #3949ab 100%)",
          color: "#fff",
        },
      }}
    >
      {/* Header logo area */}
      <Box
        sx={{
          height: 64,
          display: "flex",
          alignItems: "center",
          px: 2,
        }}
      >
        <Typography variant="h6" sx={{ fontWeight: 700 }}>
          FuturePanel
        </Typography>
      </Box>
      <Divider sx={{ borderColor: "rgba(255,255,255,0.2)" }} />

      <List sx={{ py: 2 }}>
        {menuItems.map((item) => {
          const selected = location.pathname === item.path;
          return (
            <ListItemButton
              component={Link}
              to={item.path}
              key={item.text}
              sx={{
                mb: 1,
                mx: 1,
                borderRadius: 2,
                backgroundColor: selected
                  ? "rgba(255,255,255,0.2)"
                  : "transparent",
                "&:hover": {
                  backgroundColor: "rgba(255,255,255,0.1)",
                },
              }}
              onClick={!isDesktop ? handleDrawerToggle : undefined}
            >
              <ListItemIcon sx={{ color: "#fff" }}>{item.icon}</ListItemIcon>
              <ListItemText
                primary={
                  <Typography
                    variant="body1"
                    sx={{
                      fontWeight: selected ? 600 : 400,
                      color: "#fff",
                    }}
                  >
                    {item.text}
                  </Typography>
                }
              />
            </ListItemButton>
          );
        })}
      </List>
    </Drawer>
  );
}
