import React from 'react';
import {
  Drawer,
  List,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Box,
  Typography
} from '@mui/material';
import { Link, useLocation } from 'react-router-dom';

import DashboardIcon from '@mui/icons-material/Dashboard';
import StoreIcon from '@mui/icons-material/Store';
import GroupsIcon from '@mui/icons-material/Groups';
import PaymentsIcon from '@mui/icons-material/Payments';
import HistoryEduIcon from '@mui/icons-material/HistoryEdu';
import ReceiptLongIcon from '@mui/icons-material/ReceiptLong';

const drawerWidth = 240;

export default function Sidebar() {
  const location = useLocation();

  const menuItems = [
    { text: 'Dashboard', path: '/', icon: <DashboardIcon /> },
    { text: 'Toko', path: '/toko', icon: <StoreIcon /> },
    { text: 'Investor', path: '/investor', icon: <GroupsIcon /> },
    { text: 'Dividen', path: '/dividen', icon: <PaymentsIcon /> },
    { text: 'Histori', path: '/histori', icon: <HistoryEduIcon /> },
    { text: 'Transactions', path: '/transactions', icon: <ReceiptLongIcon /> }, // admin only
  ];

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
          borderRight: '1px solid #e0e0e0',
          background: 'linear-gradient(180deg, #1565C0 30%, #ffffff 30%)',
        },
      }}
    >
      {/* Logo */}
      <Box
        sx={{
          height: 64,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: 'primary.main',
        }}
      >
        <Typography variant="h6" color="white" sx={{ fontWeight: 700 }}>
          Finance Logo
        </Typography>
      </Box>

      <List sx={{ py: 2 }}>
        {menuItems.map((item) => {
          const selected = location.pathname === item.path;
          return (
            <ListItemButton
              component={Link}
              to={item.path}
              key={item.text}
              sx={{
                mb: 1,
                mx: 1,
                borderRadius: 2,
                backgroundColor: selected ? 'rgba(21,101,192,0.15)' : 'transparent',
                '&:hover': {
                  backgroundColor: 'rgba(21,101,192,0.1)',
                },
              }}
            >
              <ListItemIcon sx={{ color: selected ? 'primary.main' : 'grey.700' }}>
                {item.icon}
              </ListItemIcon>
              <ListItemText
                primary={
                  <Typography
                    variant="body1"
                    sx={{
                      color: selected ? 'primary.main' : 'grey.800',
                      fontWeight: selected ? 600 : 400,
                    }}
                  >
                    {item.text}
                  </Typography>
                }
              />
            </ListItemButton>
          );
        })}
      </List>
    </Drawer>
  );
}
